import chai, { expect } from 'chai';
import chaiHttp from 'chai-http';
import { Application } from '../src/app';
import { ERROR_CODE } from '../src/util/ErrorCodes';
import { AuthResult } from '../src/lib/infuser-protobuf/gen/proto/author/auth_pb';
import { UserRes } from '../src/lib/infuser-protobuf/gen/proto/author/user_pb';

process.env.NODE_ENV = 'test';

chai.use(chaiHttp);
describe('authApi', () => {
  let application;
  let request;

  before(async () => {
    application = new Application()
    await application.setupDbAndServer();
    request = chai.request(application.app);
  })

  after(async () => {
    application.server.close();
  })

  it('regist - duplicate_login_id', async () => {
    /**
     * duplicate user
     */
    chai.request(application.app)
    .post('/api/oauth/regist')
    .send({
      username: "admin",
      password: "admin",
      passwordConfirm: "admin",
      name: "chunghyup",
      email: "chunghyup@gamil.com"
    })
    .end((err, res) => {
      expect(res).to.have.status(401);
      expect(res.body.code).to.equal(ERROR_CODE.REGIST[UserRes.Code.DUPLICATE_LOGIN_ID]);
    })
  });

  it('regist - password_not_matched', async () => {
    /**
     * password not match
     */
    chai.request(application.app)
    .post('/api/oauth/regist')
    .send({
      username: "asdjf9123kjfsadlfj1-2",
      password: "admin",
      passwordConfirm: "admin2",
      name: "asdrf392-0risa;dlof",
      email: "asd4129ufwadjs;f@gamil.com"
    })
    .end((err, res) => {
      expect(res).to.have.status(401);
      expect(res.body.code).to.equal(ERROR_CODE.REGIST[UserRes.Code.PASSWORD_NOT_MATCHED]);
    })
  });

  it('regist - duplicate_email', async () => {
    chai.request(application.app)
    .post('/api/oauth/regist')
    .send({
      username: "chunghyup-3",
      password: "admin2",
      passwordConfirm: "admin2",
      name: "asdrf392-0risa;dlof",
      email: "chunghyup.oh@gmail.com"
    })
    .end((err, res) => {
      expect(res).to.have.status(401);
      expect(res.body.code).to.equal(ERROR_CODE.REGIST[UserRes.Code.DUPLICATE_EMAIL]);
    })
  })
});

import express, {
  Response as ExResponse,
  Request as ExRequest,
  NextFunction,
} from "express";
import bodyParser from "body-parser";
import methodOverride from "method-override";
import compileSass from "express-compile-sass";
import path from "path";
import session from "express-session";
import morgan from "morgan";
import flash from "express-flash";
import {createConnection, ConnectionOptions} from "typeorm";
import ApplicationError from "./ApplicationError";
import cors from "cors";
import { RegisterRoutes } from './routes/routes';
import swaggerUi from "swagger-ui-express";
import BullManager from "./util/BullManager";
import * as grpc from "grpc";
import * as protoLoader from "@grpc/proto-loader";
import setupApplications from "./grpc/applications";
import swagger from './routes/swagger.json';
import ormConfig from "./config/ormConfig";
import { ValidateError } from "tsoa";
import { TokenExpiredError, JsonWebTokenError } from "jsonwebtoken";
import RedisManager from "./util/RedisManager";

const property = require("../property.json")

export class Application {
  app: express.Application;
  server;
  grpcServer;
  auth;
  constructor() {
    this.app = express();
    this.app.use(cors());
    this.app.use(bodyParser.json());
    this.app.use(bodyParser.urlencoded({extended: false}));
    this.app.use(methodOverride('_method'));
    this.app.use(session({
      resave: true,
      saveUninitialized: true,
      secret: 'long-long-long-secret-string-1313513tefgwdsvbjkvasd'
    }));

    this.app.use(flash());
    this.app.use(function(req, res, next) {
      res.locals.flashMessages = req.flash();
      next();
    });
    if (process.env.NODE_ENV !== 'test') {
      this.app.use(morgan(":remote-addr - :remote-user [:date[clf]] \":method :url HTTP/:http-version\" :status :res[content-length] :response-time ms"));
    }
  }

  setupDbAndServer = async () => {
    const conn = await createConnection(ormConfig.defaultConnection)
    const datasetConn = await createConnection(ormConfig.datasetConnection)

    const redisClient = await RedisManager.Instance.connect();

    RegisterRoutes(<express.Express>this.app);
    BullManager.setupBull(this.app);

    this.app.use("/api-docs", swaggerUi.serve, async (_req: express.Request, res: express.Response) => {
      return res.send(
        swaggerUi.generateHTML(swagger)
      );
    });

    this.app.use(function(req, res, next) {
      let err = new ApplicationError(404, 'Not Found');
      next(err);
    });

    this.app.use(function errorHandler(
      err: unknown,
      req: ExRequest,
      res: ExResponse,
      next: NextFunction
    ): ExResponse | void {
      if (err instanceof ValidateError) {
        return res.status(422).json({
          message: "Validation Failed",
          details: err?.fields,
        });
      }

      if (err instanceof JsonWebTokenError) {
        return res.status(401).json({
          message: "Token expired"
        })
      }

      if (err instanceof Error) {
        if(err.name === "ApplicationError") {
          const applicationError = <ApplicationError> err;
          return res.status(applicationError.statusCode).json({
            code: applicationError.message
          });
        }
        return res.status(500).json({
          message: "Internal Server Error",
        });
      }

      next();
    });

    await this.startServer();
    this.setupGrpcServer();
  }

  setupGrpcServer() {
    this.grpcServer = new grpc.Server();
    setupApplications(this.grpcServer);
    this.grpcServer.bind(`0.0.0.0:${property.grpcPort}`, grpc.ServerCredentials.createInsecure());
    this.grpcServer.start();
  }

  startServer(): Promise<boolean> {
    return new Promise((resolve, reject) => {
      this.server = this.app.listen(property.port, () => {
        resolve(true);
      });
    });
  }
}

export default class ApplicationError extends Error {
  statusCode: number;

  constructor(statusCode?: number, message?: string) {
    super(message);
    this.stack = (<any>new Error()).stack;
    this.name = "ApplicationError";
    this.message = message || 'Something went wrong. Please try again.';
    this.statusCode = statusCode || 500;
  }
}

