import * as grpc from "grpc";
import { LoginReq, AuthResult, AuthRes, RefreshTokenReq } from '../lib/infuser-protobuf/gen/proto/author/auth_pb';
import { AuthServiceClient } from "../lib/infuser-protobuf/gen/proto/author/auth_grpc_pb";
import ApplicationError from '../ApplicationError';
import { UserReq, UserRes } from '../lib/infuser-protobuf/gen/proto/author/user_pb';
import { UserServiceClient } from "../lib/infuser-protobuf/gen/proto/author/user_grpc_pb";
import { ERROR_CODE } from '../util/ErrorCodes';
import { RegistParams } from "../controllers/api/AuthController";

const property = require("../../property.json");

class InfuserGrpcAuthorClient {
  private static _instance: InfuserGrpcAuthorClient;

  private _authClient: AuthServiceClient;
  private _userClient: UserServiceClient;

  constructor() {
    const server = `${property.grpc.auth.host}:${property.grpc.auth.port}`
    this._authClient = new AuthServiceClient(server, grpc.credentials.createInsecure());
    this._userClient = new UserServiceClient(server, grpc.credentials.createInsecure());
  }

  public static get Instance() {
    if(!InfuserGrpcAuthorClient._instance) InfuserGrpcAuthorClient._instance = new InfuserGrpcAuthorClient();
    return this._instance;
  }

  // VALID = 0;
  // NOT_REGISTERED = -1;
  // INVALID_PASSWORD = -2;
  // WITHDRAWAL_USER = -3;
  // INVALID_TOKEN = -4;
  // INTERNAL_EXCEPTION = -9;
  public async login(username: string, password: string):Promise<AuthRes.AsObject>{
    return new Promise( (resolve, reject) => {
      const loginReq = new LoginReq();
      loginReq.setLoginId(username);
      loginReq.setPassword(password);
      this._authClient.login(loginReq, (err: grpc.ServiceError, response:AuthRes) => {
        if(err) {
          reject(new Error(err.message));
        } else if(response.getCode() !== AuthResult.VALID) {
          let messageCode = ERROR_CODE.AUTH[response.getCode()] ? ERROR_CODE.AUTH[response.getCode()] : ERROR_CODE.default
          reject(new ApplicationError(401, messageCode));
        }
        resolve(response.toObject());
      })
    })
  }

  // VALID = 0;    
  // DUPLICATE_LOGIN_ID = -1;
  // PASSWORD_NOT_MATCHED = -2;
  // DUPLICATE_EMAIL = -3;
  // INTERNAL_EXCEPTION = -99;
  public async refresh(refreshToken: string): Promise<AuthRes.AsObject> {
    return new Promise( (resolve, reject) => {
      const refreshReq: RefreshTokenReq = new RefreshTokenReq();
      refreshReq.setRefreshToken(refreshToken);
      this._authClient.refresh(refreshReq, (err: grpc.ServiceError, response: AuthRes) => {
        if(err) {
          reject(new Error(err.message));
        } else if(response.getCode() !== AuthResult.VALID) {
          reject(new ApplicationError(401, response.getMsg()));
        }
        resolve(response.toObject());
      })
    })
  }

  // VALID = 0;    
  // DUPLICATE_LOGIN_ID = -1;
  // PASSWORD_NOT_MATCHED = -2;
  // DUPLICATE_EMAIL = -3;
  // INTERNAL_EXCEPTION = -99;
  public async regist(registPrams: RegistParams): Promise<UserRes.AsObject> {
    return new Promise( (resolve, reject) => {
      const userReq = new UserReq();
      userReq.setEmail(registPrams.email);
      userReq.setLoginId(registPrams.username);
      userReq.setPassword(registPrams.password);
      userReq.setPasswordConfirmation(registPrams.passwordConfirm);
      userReq.setName(registPrams.name);
      this._userClient.signup(userReq, (err: grpc.ServiceError, response: UserRes) => {
        if(err) {
          reject(new Error(err.message));
        } else if(response.getCode() !== UserRes.Code.VALID) {
          let messageCode = ERROR_CODE.REGIST[response.getCode()] ? ERROR_CODE.REGIST[response.getCode()] : ERROR_CODE.default
          reject(new ApplicationError(401, messageCode));
        }
        resolve(response.toObject());
      })
    })
  }
}

export default InfuserGrpcAuthorClient;

import { AuthResult } from '../lib/infuser-protobuf/gen/proto/author/auth_pb';
import { UserRes } from '../lib/infuser-protobuf/gen/proto/author/user_pb';

export const ERROR_CODE = {
    default: "GLOBAL_0001",
    AUTH: {
        [AuthResult.NOT_REGISTERED]: "AUTH_0001",
        [AuthResult.INVALID_PASSWORD]: "AUTH_0002",
        [AuthResult.WITHDRAWAL_USER]: "AUTH_0003",
        [AuthResult.INVALID_TOKEN]: "AUTH_0004",
    },
    REGIST: {
        [UserRes.Code.DUPLICATE_LOGIN_ID]: "REGIST_0001",
        [UserRes.Code.PASSWORD_NOT_MATCHED]: "REGIST_0002",
        [UserRes.Code.DUPLICATE_EMAIL]: "REGIST_0003"
    }
}

import redis from 'redis';
import { InfuserUser } from '../controllers/api/AuthController';

const properties = require("../../property.json");

class RedisManager {
  private static _instance: RedisManager;

  client: redis.RedisClient;

  constructor() {}
  public static get Instance(): RedisManager {
    if(!this._instance) { this._instance = new RedisManager() }
    return this._instance;
  }


  async connect() {
    this.client = await this.createClient();
  }

  private async createClient():Promise<redis.RedisClient> {
    return new Promise((resolve, reject) => {
      const client = redis.createClient({
        host: properties.redis.host,
        port: properties.redis.port
      })

      client.on("connect", () => {
        resolve(client);
      })

      client.on("error", (err) => {
        console.error(err);
        reject(err);
      })
    })
  }

  get isConnected() {
    if(this.client) {
      return this.client.connected 
    }
    return false
  }

  private async set(key: string, value: string) {
    return new Promise((resolve, reject) => {
      this.client.set(key, value, ((err, reply) => {
        if(err) reject(err);
        resolve();
      }));
    })
  }

  private async expire(key: string, expiredIn: number) {
    return new Promise((resolve, reject) => {
      this.client.expire(key, expiredIn, ((err, reply) => {
        if(err) reject(err);
        resolve();
      }));
    })
  }

  private async get(key: string): Promise<string> {
    return new Promise((resolve, reject) => {
      this.client.get(key, ((err, reply) => {
        if(err) reject(err);
        resolve(reply);
      }));
    })
  }

  async setUserToken(infuserUser: InfuserUser) {
    await this.set(infuserUser.token, JSON.stringify(infuserUser));
    await this.expire(infuserUser.token, infuserUser.expireAt - Math.floor(Date.now() / 1000));
    return Promise.resolve();
  }

  async getUser(token) {
    const stringifyJson = await this.get(token);
    const user = JSON.parse(stringifyJson);
    return Promise.resolve(user);
  }
}

export default RedisManager;

import * as express from "express";
import * as jwt from "jsonwebtoken";
import { getRepository } from "typeorm";
import RedisManager from '../util/RedisManager';
import { inRange } from "lodash";
import { InfuserUser } from '../controllers/api/AuthController';
import ApplicationError from '../ApplicationError';

export function expressAuthentication(
  request: express.Request,
  securityName: string,
  scopes?: string[]
): Promise<InfuserUser> {
  if (securityName === "jwt") {
    const bearerToken =
      request.body.authorization ||
      request.query.authorization ||
      request.headers.authorization;
    return new Promise(async (resolve, reject) => {
      try {
        if (!bearerToken) {
          reject(new ApplicationError(401,"No token provided"));
          return;
        }

        const tokens = bearerToken.split(" ");
        if(tokens.length != 2 || tokens[0] != "Bearer") {
          reject(new ApplicationError(401,"Wrong Token Format"));
          return;
        }
        const token = tokens[1]
        const user = await RedisManager.Instance.getUser(token);
        if(user) { resolve(user); }
        else { reject(); }
      } catch (err) {
        reject(err);
      }
    });
  }
}

import * as grpc from "grpc";
import * as protoLoader from "@grpc/proto-loader";
import { getRepository, FindManyOptions, Like } from 'typeorm';
import { Application } from "../entity/manager/Application";
import { ApplicationServiceService, IApplicationServiceServer } from '../lib/infuser-protobuf/gen/proto/designer/application_grpc_pb';
import { ApplicationList as GApplicationList, ListApplicationRequest } from "../lib/infuser-protobuf/gen/proto/designer/application_pb";
import { Application as GApplication } from '../lib/infuser-protobuf/gen/proto/designer/application_pb';

class ApplicationServer implements IApplicationServiceServer {
  async listApplication(call: grpc.ServerUnaryCall<ListApplicationRequest>, callback: grpc.sendUnaryData<GApplicationList>): Promise<void> {
    try {
      var page = call.request.getPage();
      var perPage = call.request.getPerPage();
      var nameSpace = call.request.getNameSpace();

      if(!page) page = 1;
      if(!perPage) perPage = 10;

      const findOption:FindManyOptions = {
        select: ["id", "nameSpace", "title", "description" ,"status" ,"userId" ,"createdAt" ,"updatedAt"],
        skip: perPage*(page - 1),
        take: perPage,
        order: {
          id: 'ASC'
        },
        where: {}
      }

      if (nameSpace) {
        findOption.where["nameSpace"] = Like(`%${nameSpace}%`)
      }
      const applicationRepo = getRepository(Application);
      const findResult = await applicationRepo.findAndCount(findOption);


      const applicationList = new GApplicationList();
      applicationList.setTotalCount(findResult[1]);
      applicationList.setPage(page);
      applicationList.setPerPage(perPage);
      applicationList.setApplicationsList(findResult[0].map((app) => {
        const application = new GApplication();
        application.setId(app.id);
        application.setCreatedAt(app.createdAt.toDateString());
        application.setDescription(app.description);
        application.setNameSpace(app.nameSpace);
        application.setTitle(app.title);
        application.setUpdatedAt(app.updatedAt.toDateString());
        application.setUserId(app.userId);
        return application;
      }))
      callback(null, applicationList);
    } catch (err) {
      callback(err, null);
    }
  };

}
export default function setupApplications(server: grpc.Server) {  
  server.addService<IApplicationServiceServer>(ApplicationServiceService, new ApplicationServer());
}

